import React, { useState } from 'react'
import Keypad from './components/keypad'
import Result from './components/result'
import './App.scss'

 const App = () => {
    const [num, setNum] = useState('')

    const handleButton = button => {
        if (button === "=") {
            calculate()
        }
        if (button === "AC") {
            reset();
        }
        if (button === "CE") {
            backspace();
        }
    }

    const calculate = () => {
        let checkCalculate = ''
        if (num.result.includes('--')) {
            checkCalculate = num.result.replace('--', '+')
        }
        else{
            checkCalculate = num.result
        }

        try {
            setNum({
                result: (eval(checkCalculate) || "" ) + ""
            })
        } catch (error) {
            setNum({
                result: 'error'
            })
        }
    }

    const reset = () => {
        setNum({
            result: ''
        })
    }

    const backspace = () => {
        setNum({
            result: num.result.slice(0, -1)
        })
    }






   return (
     <div>
        <h1>Calculator App</h1>
        <Result Result={num.result}/>
        <Keypad onClick={handleButton}/>
     </div>
   )
 }


export default App;
