import React, { useState } from 'react'
import Keypad from './components/keypad'
import Result from './components/result'
import './App.scss'
 const App = () => {
    const [number, setNumber] = useState(
        {
            result: ""
        }
    )

    const onClick = button => {
        if (button === "=") {
            calculate();
        }
        if (button === "AC") {
            reset()
        }
        if (button === "C") {
            backspace();
        }
    }

    const calculate = () => {
        let checkResult = ''
        if (number.result.includes('--')) {
            checkResult = number.result.replace('--', '+')
        }
        else {
            checkResult = number.result
        }

        try {
            setNumber({
                result: (eval(checkResult) || "") + ""
            })

        } catch (error) {
            setNumber({
                result: 'error'
            })
        }
    }

    const reset = () => {
        setNumber({
            result: ''
        })
    }

    const backspace = () => {
        setNumber({
            result: number.result.sp
        })
    }






   return (
     <div className="calculator-body">
        <h1>Calculator App</h1>
        <Result result={state.result}/>
        <Keypad onClick={onClick}/>
     </div>
   )
 }


export default App;
