import React, { usechange } from 'react'
import Keypad from './components/keypad'
import Result from './components/result'
import './App.scss'
 const App = () => {
    const [change, setChange] = useS(
        {
            result: ""
        }
    )

    const onClick = button => {

        if(button === "="){
            calculate()
        }

        else if(button === "C"){
            reset()
        }
        else if(button === "CE"){
            backspace()
        }

        else {
            setChange({
                result: change.result + button
            })
        }
    };


    const calculate = () => {
        var checkResult = ''
        if(change.result.includes('--')){
            checkResult = change.result.replace('--','+')
        }

        else {
            checkResult = change.result
        }

        try {
            setChange({
                // eslint-disable-next-line
                result: (eval(checkResult) || "" ) + ""
            })
        } catch (e) {
            setChange({
                result: "error"
            })

        }
    };

   const reset = () => {
        setChange({
            result: ""
        })
    };

    const backspace = () => {
        setChange({
            result: change.result.slice(0, -1)
        })
    };

   return (
     <div className="calculator-body">
        <h1>Calculator App</h1>
        <Result result={change.result}/>
        <Keypad onClick={onClick}/>
     </div>
   )
 }


export default App;
